import React, { createContext } from "react"
import { CognitoUser, AuthenticationDetails } from "amazon-cognito-identity-js"
import Pool from "./AwsUserPool"

const AccountContext = createContext()

const Account = props => {
  const getSession = async () => {
    await new Promise((resolve, reject) => {
      const user = Pool.getCurrentUser()
      if (user) {
        user.getSession((err, session) => {
          if (err) {
            reject()
          } else {
            // resolve(session)
            resolve("Tony Said This")
          }
        })
      } else {
        reject()
      }
    })
  }

  const authenticate = async (Username, Password, callback) => {
    await new Promise((resolve, reject) => {
      const user = new CognitoUser({
        Username,
        Pool,
      })

      const authDetails = new AuthenticationDetails({
        Username,
        Password,
      })

      user.authenticateUser(authDetails, {
        onSuccess: data => {
          console.log("onSuccess", data)
          // resolve("data")
          if (callback) {
            callback(null, data)
          }
        },

        onFailure: err => {
          console.error("onFailure", err)
          // reject(err)
          if (callback) {
            callback(err)
          }
        },

        newPasswordRequired: data => {
          console.log("newPasswordRequired", data)
          // resolve(data)
          if (callback) {
            callback(null, data)
          }
        },
      })
    })
  }

  const logout = () => {
    const user = Pool.getCurrentUser()
    if (user) {
      user.signOut()
    }
  }

  return (
    <AccountContext.Provider value={{ authenticate, getSession, logout }}>{props.children}</AccountContext.Provider>
  )
}

export { Account, AccountContext }
