import React, { useState, useEffect } from "react"
import styled from "@emotion/styled"
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom"

// import WeatherCard from "./components/WeatherCard/component"
// import { isElementOfType } from "react-dom/test-utils"
import Toolbar from "./components/Toolbar/Toolbar"
import SideDrawer from "./components/SideDrawer/SideDrawer"
import Backdrop from "./components/Backdrop/Backdrop"
import MainContentArea from "./components/MainContentArea/MainContentArea"
import Home from "./components/MainContentArea/Home"
import Products from "./components/MainContentArea/Products"
import UsersTable from "./components/MainContentArea/UsersTable"

function App() {
  // Tootlbar and side bar setup

  const toolBarHeight = "50px"

  let toolBarItems = {
    logo: "KenzTV",
    navItemList: [
      { reference: "/", name: "Home" },
      { reference: "/Users", name: "Users" },
    ],
  }

  let sideBarItemList = [
    { reference: "/Products", name: "Products" },
    { reference: "/Users", name: "Users" },
  ]

  const App = styled.div`
    margin: 0 auto;
    color: white;
    height: 100%;
  `
  // Tootlbar and side bar functionality

  const [sideBar, setSideBar] = useState(false)
  // const [sideDrawerTemp, setSideDrawerTemp] = useState(null)
  // const [backDropTemp, setBackDropTemp] = useState(null)

  const drawerToggleClickHandler = () => {
    setSideBar(!sideBar)
  }

  return (
    <Router>
      <App>
        <Toolbar
          toolBarItems={toolBarItems}
          toolBarHeight={toolBarHeight}
          drawerToggleClickHandler={drawerToggleClickHandler}
        />
        <SideDrawer
          show={sideBar}
          click={drawerToggleClickHandler}
          sideBarItemList={sideBarItemList}
        />
        {sideBar ? <Backdrop click={drawerToggleClickHandler} /> : <div></div>}

        <Switch>
          <Route exact path="/">
            <MainContentArea toolBarHeight={toolBarHeight}>
              <Home />
            </MainContentArea>
          </Route>
          <Route exact path="/Products">
            <Products />
          </Route>
          <Route exact path="/Users">
            <UsersTable />
          </Route>
        </Switch>
      </App>
    </Router>
  )
}

export default App
