import styled from "@emotion/styled"
// import WeatherCard from "./components/WeatherCard/component"
// import { isElementOfType } from "react-dom/test-utils"
import Toolbar from "./components/Toolbar/Toolbar"
import ContentArea from "./components/MainContentArea/ContentArea"
import SideDrawer from "./components/SideDrawer/SideDrawer"
import Backdrop from "./components/Backdrop/Backdrop"

function App() {
  // Tootlbar and side bar setup

  const toolBarHeight = "50px"

  let toolBarItems = {
    logo: "KenzTV",
    navItemList: [
      { reference: "/", name: "Products" },
      { reference: "/", name: "Users" },
    ],
  }

  let sideBarItemList = [
    { reference: "/", name: "Products" },
    { reference: "/", name: "Users" },
  ]

  const App = styled.div`
    margin: 0 auto;
    color: white;
    height: 100%;
  `
  // Tootlbar and side bar functionality

  const [sideBar, setSideBar] = useState(false)
  // const [sideDrawerTemp, setSideDrawerTemp] = useState(null)
  // const [backDropTemp, setBackDropTemp] = useState(null)

  const drawerToggleClickHandler = () => {
    setSideBar(!sideBar)
  }

  // useEffect(() => {
  //   if (sideBar) {
  //     setSideDrawerTemp("<SideDrawer sideBarItemList={sideBarItemList} />")
  //     setBackDropTemp("<BackDropTemp = <Backdrop />")
  //   } else {
  //     setSideDrawerTemp(null)
  //     setBackDropTemp(null)
  //   }
  // }, [sideBar])

  return (
    <App>
      <Toolbar
        toolBarItems={toolBarItems}
        toolBarHeight={toolBarHeight}
        drawerToggleClickHandler={drawerToggleClickHandler}
      />
      {sideBar ? <SideDrawer sideBarItemList={sideBarItemList} /> : <div></div>}
      {sideBar ? <Backdrop /> : <div></div>}
      <ContentArea toolBarHeight={toolBarHeight} />
      {/* <h1>The user is {userList}</h1> */}
      {/* <WeatherCard temperature={-20} /> */}
    </App>
  )
}

export default App
