import React from "react"
import styled from "@emotion/styled"

const backDrop = (props) => {
  const BackDrop = styled.div`
    postion: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    z-index: 100;
  `

  return <BackDrop />
}

export default backDrop
